package SwitchCase;

public class WeekDays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=1;
	// String char int  ---> allowed in switch case only 	
	// we can't write condition as case	
		switch(n) {
		
		case 1:System.out.println("Monday");
		break;
		case 2:System.out.println("Tuesday");
		break;
		default : System.out.println("Sorry please enter valid day week have 7 days only");
		}

	}

}
